import { createStore } from 'vuex';

// Rastgele isimler listesi
const horseNames = [
  'Thunder', 'Lightning', 'Storm', 'Blaze', 'Shadow', 'Spirit', 'Majesty',
  'Champion', 'Silver', 'Gold', 'Comet', 'Wind', 'Mystic', 'Starlight',
  'Courage', 'Freedom', 'Victory', 'Flash', 'Valor', 'Glory'
];

// Atları oluşturacak fonksiyon
function generateHorses() {
  const horses = [];
  const usedNames = new Set();

  for (let i = 1; i <= 20; i++) {
    let name;
    do {
      name = horseNames[Math.floor(Math.random() * horseNames.length)];
    } while (usedNames.has(name));
    usedNames.add(name);

    horses.push({
      id: i,
      name: name,
      color: getRandomColor(),
      condition: Math.floor(Math.random() * 100) + 1,
    });
  }
  return horses;
}

// Rastgele renk oluşturacak fonksiyon
function getRandomColor() {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

export default createStore({
  state: {
    horses: JSON.parse(localStorage.getItem('horses')) || [],
  },
  mutations: {
    setHorses(state, horses) {
      state.horses = horses;
      localStorage.setItem('horses', JSON.stringify(horses)); // Atları yerel depolamaya kaydet
    },
  },
  actions: {
    generateHorses({ commit, state }) {
      if (state.horses.length === 0) {
        const horses = generateHorses();
        commit('setHorses', horses);
      }
    },
  },
});
