// Import necessary modules
import { createStore } from 'vuex';

// Function to generate horses
function generateHorses() {
  const horses = [];
  for (let i = 1; i <= 20; i++) {
    horses.push({
      id: i,
      color: getRandomColor(),
      condition: Math.floor(Math.random() * 100) + 1,
      position: 0,
      image: require(`@/assets/horse${i}.png`),
    });
  }
  return horses;
}

// Function to generate a random color
function getRandomColor() {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

// Local storage key
const LOCAL_STORAGE_KEY = 'horse-racing-game';

// Function to generate race schedule
function generateRaceSchedule(horses) {
  const distances = [1200, 1400, 1600, 1800, 2000, 2200];
  const schedule = distances.map(distance => {
    const selectedHorses = [];
    const horseIds = new Set();

    for (let i = 1; i <= 10; i++) {
      let horse;
      do {
        horse = horses[Math.floor(Math.random() * horses.length)];
      } while (horseIds.has(horse.id));
      selectedHorses.push(horse);
      horseIds.add(horse.id);
    }

    return {
      distance,
      horses: selectedHorses,
    };
  });
  return schedule;
}


  export default createStore({
    state: {
      horses: [], // At listesi
      raceSchedule: [], // Yarış programı
    },
    mutations: {
      setHorses(state, horses) {
        state.horses = horses;
      },
      setRaceSchedule(state, schedule) {
        state.raceSchedule = schedule;
      },
      updateHorsePosition(state, { horseId, position }) {
        const horse = state.horses.find(h => h.id === horseId);
        if (horse) {
          horse.position = position;
        }
      },
      clearRaceResults(state) {
        state.raceResults = [];
      },
    },
    actions: {
      generateHorses({ commit }) {
        // Atları oluştur ve Vuex store'a kaydet
        const horses = []; // Atları oluşturmak için gerekli kod
        commit('setHorses', horses);
      },
      startRace({ commit, state }) {
        commit('clearRaceResults'); // Yarış sonuçlarını temizle

        // Yarış programını oluştur
        const schedule = []; // Yarış programını oluşturmak için gerekli kod
        commit('setRaceSchedule', schedule);

        // Her bir yarışı sırayla başlat
        schedule.forEach((race, index) => {
          setTimeout(() => {
            // Her at için yeni pozisyon belirle ve Vuex store'a kaydet
            race.horses.forEach(horse => {
              const newPosition = horse.position + Math.random() * 10;
              commit('updateHorsePosition', { horseId: horse.id, position: newPosition });
            });

            // Yarışın kazananını belirle ve sonucu Vuex store'a kaydet
            const winner = race.horses.reduce((prev, current) => (prev.position > current.position) ? prev : current);
            const result = `Winner: Horse ${winner.id}`;
            commit('addRaceResult', result);
          }, index * 5000); // Her yarış 5 saniye arayla başlat
        });
      },
    },
  });
