import { createStore } from 'vuex';

// Rastgele isimler listesi
const horseNames = [
  'Thunder', 'Lightning', 'Storm', 'Blaze', 'Shadow', 'Spirit', 'Majesty',
  'Champion', 'Silver', 'Gold', 'Comet', 'Wind', 'Mystic', 'Starlight',
  'Courage', 'Freedom', 'Victory', 'Flash', 'Valor', 'Glory'
];

// Atları oluşturacak fonksiyon
function generateHorses() {
  const horses = [];
  const usedNames = new Set();

  for (let i = 1; i <= 20; i++) {
    let name;
    do {
      name = horseNames[Math.floor(Math.random() * horseNames.length)];
    } while (usedNames.has(name));
    usedNames.add(name);

    horses.push({
      id: i,
      name: name,
      color: getRandomColor(),
      condition: Math.floor(Math.random() * 100) + 1,
      position: 0,
      image: require(`@/assets/horse${i}.png`),
    });
  }
  return horses;
}

// Rastgele renk oluşturacak fonksiyon
function getRandomColor() {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

export default createStore({
  state: {
    horses: JSON.parse(localStorage.getItem('horses')) || [],
    raceSchedule: [],
    raceResults: [],
  },
  mutations: {
    setHorses(state, horses) {
      state.horses = horses;
      localStorage.setItem('horses', JSON.stringify(horses)); // Atları yerel depolamaya kaydet
    },
    setRaceSchedule(state, schedule) {
      state.raceSchedule = schedule;
    },
    updateHorsePosition(state, { horseId, position }) {
      const horse = state.horses.find(h => h.id === horseId);
      if (horse) {
        horse.position = position;
      }
    },
    addRaceResult(state, result) {
      state.raceResults.push(result);
    },
    clearRaceResults(state) {
      state.raceResults = [];
    },
  },
  actions: {
    generateHorses({ commit, state }) {
      const horses = generateHorses();
      commit('setHorses', horses);
    },
    startRace({ commit, state }) {
      commit('clearRaceResults');
      const schedule = generateRaceSchedule(state.horses);
      commit('setRaceSchedule', schedule);

      schedule.forEach((race, index) => {
        setTimeout(() => {
          race.horses.forEach(horse => {
            const newPosition = horse.position + Math.random() * 10;
            commit('updateHorsePosition', { horseId: horse.id, position: newPosition });
          });
          const winner = race.horses.reduce((prev, current) => (prev.position > current.position) ? prev : current);
          const result = `Winner: Horse ${winner.id} (${winner.name})`;
          commit('addRaceResult', result);
        }, index * 5000); // Her yarışı 5 saniye arayla başlatır
      });
    },
  },
});
